<?php
require_once 'PHPUnit/Framework.php';

require_once dirname(__FILE__).'/../../../../test/bootstrap/unit.php';
require_once dirname( __FILE__ ).'/../../bootstrap.php';

/**
 * Test class for Importer.
 * Generated by PHPUnit on 2010-01-18 at 17:42:41.
 */
class ImporterTest extends PHPUnit_Framework_TestCase
{
  /**
   * @var Importer
   */
  protected $object;

  /**
   * Sets up the fixture, for example, opens a network connection.
   * This method is called before a test is executed.
   */
  protected function setUp()
  {
    $this->object = new MockImporter;
  }

  /**
   * Tears down the fixture, for example, closes a network connection.
   * This method is called after a test is executed.
   */
  protected function tearDown()
  {
  }

  public function testLoadDataFailsIfNoImportSaver()
  {
    try
    {
      $this->object->loadData();
      $this->fail();
    }
    catch( ImportException $e ){}
  }

  /**
   * test setSaver() only allows objects of type ImportSaver
   */
  public function testSetSaverAllowsOnlyImportSaverTypes()
  {
    try
    {
      $this->object->setSaver( 'foo' );
      $this->fail();
    }
    catch( ImportException $e ){}
  }

  /**
   * 
   */
  public function testLoadsData()
  {
    $this->assertFalse( $this->object->hasData() );
    $this->object->setSaver( new ImportSaver() );
    
    try
    {
      $this->object->loadData();
      $this->fail();
    }
    catch( ImportException $e ){} //saver->save() successfully called
    
    $this->assertTrue( $this->object->hasData() );
  }
}

class MockImporter extends Importer
{
  protected function load()
  {
    return 'data';
  }
}

class ImportSaver
{
  public function save()
  {
    throw new ImportException( 'Trying to save!' );
  }
}
?>
