<?php
require_once 'PHPUnit/Framework.php';

require_once dirname(__FILE__).'/../../../../test/bootstrap/unit.php';
require_once dirname( __FILE__ ).'/../../bootstrap.php';

/**
 * Test class for FourDImporterclass.
 * Generated by PHPUnit on 2010-01-18 at 12:00:25.
 */
class SoapImporterTest extends PHPUnit_Framework_TestCase
{
  /**
   * @var SoapImporter
   */
  protected $object;

  /**
   * Sets up the fixture, for example, opens a network connection.
   * This method is called before a test is executed.
   */
  protected function setUp()
  {
    $this->object = new GoodSoapImporter( 'http://192.9.200.204:8080/4DWSDL' );
    ProjectN_Test_Unit_Factory::createSqliteMemoryDb();
  }

  /**
   * Tears down the fixture, for example, closes a network connection.
   * This method is called after a test is executed.
   */
  protected function tearDown()
  {
    ProjectN_Test_Unit_Factory::destroySqliteMemoryDb();
  }

  /**
   * hasErrors() should be false if soap call successful
   */
  public function testNoErrorsIfSoapCallSuccess()
  {
    $this->object->setSaver( new ImportSaver() );
    $this->object->loadData();
    $this->assertFalse( $this->object->hasErrors() );
  }

  /**
   * hasErrors() should be false if soap call fails
   */
  public function testErrorsIfSoapCallFail()
  {
    $this->object = new BadSoapImporter( 'http://192.9.200.204:8080/4DWSDL' );
    $this->object->setSaver( new ImportSaver() );
    $this->object->loadData();
    $this->assertTrue( $this->object->hasErrors() );
  }
}

class GoodSoapImporter extends SoapImporter
{
  protected function getSoapFunction()
  {
    return 'WebService_SendFilms';
  }
}

class BadSoapImporter extends SoapImporter
{
  protected function getSoapFunction()
  {
    return 'LimeInTheCoconut';
  }
}
