    $this->assertNotNull( $results[ 0 ][ 'longitude' ], 'Store has longitude value' );
    $this->assertNotNull( $results[ 0 ][ 'longitude' ], 'Store has longitude value' );

    $this->assertArrayHasKey( 'vendor_category', $results[ 0 ], 'Store has vendor_category field' );
