    $this->assertArrayHasKey( 'vendor_poi_id', $results[ 0 ], 'Store has vendor_poi_id field' );
    $this->assertNotNull( $results[ 0 ][ 'vendor_poi_id' ], 'Store has vendor_poi_id value' );

    $this->assertArrayHasKey( 'vendor_poi_id', $results[ 0 ], 'Store has vendor_poi_id field' );
    $this->assertNotNull( $results[ 0 ][ 'vendor_poi_id' ], 'Store has vendor_poi_id value' );

